String.prototype.args = function()
{
	var arg = arguments;
	
	var ret = this;
	
	ret = ret.replace(/(%[0-9]+)/g, "($1)"); // for %10 etc
	
	for (var i = 0; i < arg.length; i++)
	{	
		ret = ret.replace(new RegExp("\\(%" + (i + 1) + "\\)", "g"), arg[i].toString());
	}
	
	return ret;
};

