function say(message, channel)
{
	if (oaTimer === undefined)
	{
		oaTimer = sys.setTimer(function() { oaCounter = 0; oaTimer = undefined; }, settings.overactiveSeconds, false);
	}
	

	if (oaCounter < settings.overactiveLimit)
	{
		oaCounter++;
		
		if (channel === undefined)
		{
			channel = client.currentChannel();
		}

		network.sendChanMessage(channel, message);
	}
	else if (oaCounter == settings.overactiveLimit) // i think i had reason for == but i cant remember :0
	{
		printMessage("Ooh, saved you! You almost passed your overactive limit!");
		oaCounter++;
	}
}

function botHTML(timestamp, colon, symbol)
{
	if (timestamp === undefined)
		timestamp = true;
	if (colon === undefined)
		colon = true;
	if (symbol === undefined)
		symbol = true;

	return "<font color='" + settings.botColour + "'>" + (timestamp ? "<timestamp />" : "") + "<b>" + (symbol ? settings.botSymbol : "")
		+ Utilities.escapeHTML(settings.botName) + (colon ? ":" : "") + "</font></b>";
}

function cmp(x1, x2)
{
	if (typeof x1 !== typeof x2)
	{
		return false;
	}
	else if (typeof x1 === "string")
	{
		if (x1.toLowerCase() === x2.toLowerCase())
		{
			return true;
		}
	}
	return x1 === x2;
}

Array.prototype.indexOf = function (item, caseSensitive)
{	
	if (caseSensitive === undefined)
	{
		caseSensitive = false;
	}

	for (var i = 0; i < this.length; i++)
	{
		if ((cmp(this[i], item) && !caseSensitive) || this[i] === item)
		{
			return i;
		}
	}

	return -1;
};

Array.prototype.format = function(fn, num)
{
	var ret = [];
	
	if (!num)
	{
		num = false;
	}
		
	for (var i = 0; i < this.length; i++)
	{
		var formatted = eval('"' + fn.replace(/%i/g, this[i]) + '"'); // quotes or it thinks its a var hahas
		
		if (num)
			formatted = parseFloat(num);
			
		ret.push(formatted);
	}
	
	return ret;
};

Array.prototype.copy = function()
{
	return this.slice(0);
};

Array.prototype.randomItem = function()
{
	if (this.length === 0)
		return undefined;
		
	var r = Utilities.randomInt(this.length);
	
	return this[r];
};

Array.prototype.clean = function(deleteValue)
{
	if (deleteValue)
	{
		var ret = this.copy();
		for (var i = 0; i < ret.length; i++)
		{
			if (ret[i] == deleteValue)
			{         
				ret.splice(i, 1);
				i--;
			}
		}
		
		return ret;
	}
	else
	{
		var ret = this.copy();
		for (var i = 0; i < ret.length; i++)
		{
			if (!ret[i])
			{
				ret.splice(i, 1);
				i--;
			}
		}
		
		return ret;
	}
};

Array.prototype.reverse = function()
{
	var ret = [];
	
	for (var i = this.length - 1; i >= 0; i--)
	{
		ret.push(this[i]);
	}
	
	return ret;
}

String.prototype.indexOf = function(str, caseIns)
{
	if (str === undefined || str.length === 0 || str.length > this.length)
		return -1;
	if (caseIns === undefined)
		caseIns = true;
	if (cmp(str, this))
		return 0;

	for (var i = 0; i < this.length; i++)
	{
		if ((cmp(this.substr(i, str.length), str) && caseIns) || (this.substr(i, str.length) === str && !caseIns))
		{
			return i;
		}
	}

	return -1;
};

String.prototype.startsWith = function(text, ins)
{
	if (ins === undefined)
		ins = false;
		
	var str = this;
	
	if (text.length > str.length)
		return false;
	
	if (!ins)
		return str.substr(0, text.length) === text;
	else
		return cmp(str.substr(0, text.length), text)
};

String.prototype.startsWithOne = function(arr, ins)
{
	var arg = arr;
	
	for (var i = 1; i < arg.length; i++)
	{
		if (this.startsWith(arg[i], ins))
		{
			cache.startsWithWhich = arg[i];
			return true;
		}
	}
	
	return false;
};

String.prototype.endsWith = function(text, ins)
{
	if (ins === undefined)
		ins = false;
		
	var str = this;
	
	if (text.length > str.length)
		return false;
		
	if (!ins)
		return str.substr(str.length - text.length) === text;
	else
		return cmp(str.substr(str.length - text.length), text);
};

String.prototype.trimString = function(str)
{
	if (str === undefined)
		str = " ";
		
	var ret = this;
	
	while (ret.startsWith(str))
	{
		if (ret.length < str.length)
		{
			return ret;
		}
		
		ret = ret.substr(str.length);
	}
	
	while(ret.endsWith(str))
	{
		if (ret.length < str.length)
		{
			return ret;
		}
		
		ret = ret.substr(0, ret.length - str.length);
	}
	
	return ret;
};

String.prototype.contains = function(str)
{
	return this.indexOf(str) !== -1;
};

Array.prototype.contains = function(item)
{
	return this.indexOf(item) !== -1;
};


Settings =
({
	load: function(json)
	{
		if (json === undefined)
		{
			if (sys.getFileContent(settingsPath) === "")
			{
				// load default //
				settings = defaults;
			}
			else
			{
				settings = JSON.parse(Utilities.readFile(settingsPath));
			}
		}
		else
		{
			settings = JSON.parse(json);
		}
		
		this.save();
	},
	save: function()
	{
		for (var key in defaults)
		{
			if (defaults.hasOwnProperty(key) && !settings.hasOwnProperty(key)) // defaults has values that settings don't, so we'll need to add them in!
			{
				settings[key] = defaults[key];
			}
			else // actual setting, check to make sure its not broke
			{
				if (typeof(settings[key]) !== typeof(defaults[key]))
				{
					settings[key] = defaults[key];
					printMessage(key + " was reset due to a type error! ...It's your fault!", false);
				}
			}
		}
		
		if (settings.cleanSettings)
		{
			for (var key in settings)
			{
				if (settings.hasOwnProperty(key) && !defaults.hasOwnProperty(key))
				{
					delete settings[key]; // cleans up unused settings
				}
			}
		}
		
		Utilities.writeFile(settingsPath, JSON.stringify(settings));
	}
});

Emotes =
({
	load: function(force)
	{
		if (force === undefined)
		{
			force = false;
		}
		
		Utilities.appendFile(emotesPath, "");
		
		if (Utilities.readFile(emotesPath) === "" || force)
		{
			// get emotes
			
			printMessage("Hang on, getting emotes... This could take a sec...", false);
			
			var ejson = Utilities.webCall(emotesUrl);
			
			if (ejson.length < 1)
			{
				printMessage("There was a problem downloading emotes. Turning emotes off.", false);
				settings.emotes = false; // lol this was not here for several month just comment say "turn off" im rly bad
				return;
			}
			
			printMessage("Got 'em! See your emotes with %1!".args(Utilities.commandLink(settings.commandSymbol + "emotes", "emotes")), true);
			
			Utilities.writeFile(emotesPath, ejson);
			emotes = JSON.parse(ejson);
		}
		else
		{
			emotes = JSON.parse(Utilities.readFile(emotesPath));
		}
		
		emoteString = "";
		
		for (var x in emotes)
		{
			if (emotes.hasOwnProperty(x))
			{
				var emote = "<a href='po:appendmsg/:%1:'>%2</a> ".args(x, Emotes.emote(x));
				emoteString += emote;
				emoteList.push(emote);
			}
		}
	},
	parse: function(text, force)
	{
		if (!settings.emotes && !force)
		{
			return text;
		}
		
		var ret = text;
		
		// we need to check to see if they did it in fullwidth, but we shouldn't just unfullwidth the whole thing //
		// ahahahahaha you said "did it" //
		
		ret = ret.replace(/：([ｑｗｅｒｔｙｕｉｏｐａｓｄｆｇｈｊｋｌｚｘｃｖｂｎｍＱＷＥＲＴＹＵＩＯＰＡＳＤＦＧＨＪＫＬＺＸＣＶＢＮＭ１２３４５６７８９０＋－＿．＇！？　]+)：/g, function(match)
		{
			var _match = Utilities.unfullwidth(match).toLowerCase();
			
			
			if (Emotes.check(_match))
			{
				return _match;
			}
			
			return match;
		});
		
		return ret.replace(/\:([a-zA-Z0-9\+\-_\.'\!\?\s]+)\:/g, function(match) // extra things are for pokenames
		{
			var name = match.substr(1, match.length - 2).toLowerCase();
			
			// special cases //
			
			if (name === "unown-a")
			{
				name = "unown";
			}
			
			// now return //
			
			if (Emotes.check(name))
			{
				return Emotes.emote(name);
			}
			
			return match;
		});
	},
	
	emote: function(name)
	{
		var pname = name.replace(/_/g, " ");
		
		if (emotes.hasOwnProperty(name))
		{
			return "<img src='" + emotes[name] + "'>";
		}
		else if (sys.pokeNum(pname) > 0 || cmp(pname, "missingno"))
		{
			return "<img src='icon:" + sys.pokeNum(pname) + "'>";
		}
	},
	
	check: function(name)
	{
		var pname = name.replace(/_/g, " ");
		return emotes.hasOwnProperty(name) || sys.pokeNum(pname) > 0 || cmp(pname, "missingno");
	}
});


Plugins =
({
	hooks: {},
	load: function()
	{
		var files = sys.filesForDirectory(pluginsPath);
		
		if (typeof(files) === "undefined")
		{
			sys.makeDir(logPath);
			files = [];
		}
		
		for (var i = 0; i < files.length; i++)
		{
			if (!files[i].endsWith(".js"))
			{
				continue;
			}
			
			try
			{
				eval(Utilities.readFile(pluginsPath + files[i]));
				printMessage("Loaded plugin: " + files[i], false);
			}
			catch (err)
			{
				printMessage("There was an error in loading plugin " + files[i] + "!", false);
				printMessage("Error: " + err, false);
				
				if (err.lineNumber)
				{
					printMessage("On line: " + err.lineNumber, false);
				}
			}
		}
	},
	hook: function(event, fn)
	{
		// how hook works: //
		// we have a json object which contains arrays of functions. //
		// we get the right array and append the function to be called by callHooks //
		
		if (!this.hooks.hasOwnProperty(event))
		{
			this.hooks[event] = [];
		}
		
		this.hooks[event].push(function(data, scope)
		{
			if (data === undefined)
				data = {};
			if (scope === undefined)
				scope = globe; // will this ever be useful??
				
			fn.call(scope, data); // still call since obj
		});
	},
	callHooks: function(event, data, scope)
	{
		// how callHooks works: //
		// we have a json object which contains arrays of functions. //
		// we get the right array from the event name and cycle through and call all of the hooked functions //
		
		if (!this.hooks.hasOwnProperty(event))
		{
			this.hooks[event] = [];
		}
		
		for (var i = 0; i < this.hooks[event].length; i++)
		{
			try
			{
				this.hooks[event][i](data, scope); // calls function
			}
			catch (err)
			{
				// we could make this more traceable but eh //
				
				printMessage("Error in hook function " + event + i + ": " + err);
				printMessage("On line: " + err.lineNumber);
			}
		}
	}
});



